import requests

url = "https://{host-domain}/api/authentication/login"

headers = {
  "Content-Type": "application/json"
}

payload = {
  "username": "****",
  "password": "****"
}
response = requests.post(url, headers=headers, json=payload)

data = response.json()
print(data)

import java.net.*;
import java.net.http.*;
import java.util.*;

public class App {
  public static void main(String[] args) throws Exception {
    var httpClient = HttpClient.newBuilder().build();
    var payload = String.join("\n"
"{"
,"  \"username\": \"****\","
,"  \"password\": \"****\""
,"}"
    );


    var host = "https://{host-domain}";
    var pathname = "/api/authentication/login";
    var request = HttpRequest.newBuilder()
      .POST(HttpRequest.BodyPublishers.ofString(payload))
      .uri(URI.create(host + pathname ))
      .header("Content-Type", "value")
      .build();

    var response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());

    System.out.println(response.body());
  }
}

import fetch from 'node-fetch';

async function run() {

const resp = await fetch(
`https://{host-domain}/api/authentication/login`,
{
  "method": "POST",
  "headers": {
    "Content-Type": "application/json"
  },
  body: JSON.stringify({
    "username": "****",
    "password": "****"
  })
}
);

const data = await resp.json();
console.log(data);
}

run();

const resp = await fetch(
`https://{host-domain}/api/authentication/login`,
{
  "method": "POST",
  "headers": {
    "Content-Type": "application/json"
  },
  body: JSON.stringify({
    "username": "****",
    "password": "****"
  })
}
);

const data = await resp.json();
console.log(data);

/**
 * Requires libcurl
 */

$curl = curl_init();

$payload = array(
  "username" => "****",
  "password" => "****"
);
$payload = array(
  "phone_number" => "+919999999999",
  "message" => "string"
);

curl_setopt_array($curl, [
  CURLOPT_HTTPHEADER => [
  "Content-Type: application/json"
],
  CURLOPT_POSTFIELDS => json_encode($payload),
  CURLOPT_URL => "https://{host-domain}/api/authentication/login",
  CURLOPT_RETURNTRANSFER => true,
  CURLOPT_CUSTOMREQUEST => "POST",
]);

$response = curl_exec($curl);
$error = curl_error($curl);

curl_close($curl);

if ($error) {
  echo "cURL Error #:" . $error;
} else {
  echo $response;
}

